function secureGetUrlContents($url, $searchString) {\n    $ch = curl_init($url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    // Ensure SSL verification is on\n    curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true);\n    curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 2);\n    $response = curl_exec($ch);\n    $httpcode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n    curl_close($ch);\n\n    if ($httpcode == 200) {\n        if (strpos($response, $searchString) === false) {\n            // The string was not found, do X\n            echo "The string '$searchString' was not found.";\n            // Add your action here\n        } else {\n            echo "The string '$searchString' was found.";\n        }\n    } else {\n        echo "Failed to retrieve the URL. HTTP Code: $httpcode";\n    }\n}\n\n$url = "http://example.com";\n$searchString = "maybe baby love you";\nsecureGetUrlContents($url, $searchString);