function preparedQuery($ids) {\n    // Create a string of placeholders for the prepared statement\n    $placeholders = implode(',', array_fill(0, count($ids), '?'));\n\n    // Prepare the query\n    $sql = "SELECT `age`, `name` FROM `people` WHERE id IN ($placeholders)";\n    $stmt = $mysqli->prepare($sql);\n\n    // Bind the parameters dynamically\n    $types = str_repeat('i', count($ids)); // Assuming all IDs are integers\n    $stmt->bind_param($types, ...$ids);\n\n    // Execute the query\n    $stmt->execute();\n\n    // Get the results\n    $result = $stmt->get_result();\n    $data = $result->fetch_all(MYSQLI_ASSOC);\n\n    return $data;\n}\n\n// Example usage\n$ids = [12, 45, 65, 33];\n$data = preparedQuery($ids);\nforeach ($data as $row) {\n    echo $row['age'] . ' ' . $row['name'] . PHP_EOL;\n}