$message = (new Swift_Message('Hello, World!'))\n    ->setFrom(['your_email@example.com' => 'Your Name'])\n    ->setBody('Hello, World!', 'text/plain');\n\n$transport = (new Swift_SmtpTransport('smtp.example.com', 587))\n    ->setUsername('your_email@example.com')\n    ->setPassword('your_password');\n\n$mailer = new Swift_Mailer($transport);\n\n// Assuming $subscribers is an array of 50,000-100,000 subscriber email addresses\nforeach ($subscribers as $subscriber) {\n    $message->setTo($subscriber);\n    $mailer->send($message);\n}