// Define an entity\nuse Doctrine\ORM\Mapping AS ORM;\n\n/**\n * @ORM\Entity\n * @ORM\Table(name="users")\n */\nclass User {\n    /**\n     * @ORM\Id\n     * @ORM\GeneratedValue\n     * @ORM\Column(type="integer")\n     */\n    private $id;\n\n    /**\n     * @ORM\Column(type="string")\n     */\n    private $name;\n\n    // ...\n}\n\n// Use the entity\n$user = new User();\n$user->setName('John Doe');\n$entityManager->persist($user);\n$entityManager->flush();