// emails.php ( script to be executed by cron job )\n\n// Fetch a limited number of emails from the database\n$stmt = $pdo->prepare('SELECT * FROM emails WHERE sent = 0 LIMIT 10');\n$stmt->execute();\n$emails = $stmt->fetchAll();\n\n// Loop through the emails and send them\nforeach ($emails as $email) {\n    // Send email using a secure method (e.g., PHPMailer)\n    // ...\n\n    // Update the email status to 'sent'\n    $stmt = $pdo->prepare('UPDATE emails SET sent = 1 WHERE id = :id');\n    $stmt->bindParam(':id', $email['id']);\n    $stmt->execute();\n}