<?php\n  $USERNAME = '';   //database username\n  $PASSWORD = '';    //database password\n  $DATABASE = '';   //database name\n  $URL = '';        //database location\n\n  if(isset($_GET['getTutorials'])) {\n    $conn = mysql_connect($URL, $USERNAME, $PASSWORD);\n    if (!$conn) {\n      $error = array('error' => 'Unable to connect to database');\n      echo json_encode($error);\n      exit;\n    }\n\n    mysql_select_db($DATABASE, $conn) or $error = array('error' => 'Unable to select database');\n\n    $returnArray = array();\n\n    $query = 'SELECT * FROM Tutorials';\n    $result = mysql_query($query, $conn);\n\n    if (!$result) {\n      $error = array('error' => 'Error executing query');\n      echo json_encode($error);\n      exit;\n    }\n\n    while($row = mysql_fetch_assoc($result)) {\n      array_push($returnArray, $row);\n    }\n\n    mysql_close($conn);\n    echo json_encode($returnArray);\n  }\n  elseif(isset($_GET['setTutorials'])) {\n     $jsonString = urldecode($_GET['jsonSendData']);\n     $jsonString = str_replace("\\", "", $jsonString);\n     $data = json_decode($jsonString, true);\n\n     $conn = mysql_connect($URL, $USERNAME, $PASSWORD);\n     if (!$conn) {\n       $error = array('error' => 'Unable to connect to database');\n       echo json_encode($error);\n       exit;\n     }\n\n     mysql_select_db($DATABASE, $conn) or $error = array('error' => 'Unable to select database');\n\n     foreach ($data as $tutorialEntry) {\n       $query = sprintf(\n        'UPDATE Tutorials SET rating = "%s" WHERE id = "%s"',\n       mysql_real_escape_string($tutorialEntry['rating']),\n       mysql_real_escape_string($tutorialEntry['id']));\n\n       $result = mysql_query($query, $conn);\n\n       if(!$result) {\n         $error = array('error' => mysql_error());\n         echo json_encode($error);\n         exit;\n       }\n     }\n\n     mysql_close($conn);\n     echo json_encode(array('success' => 'Database updated'));\n   }\n?>