function __construct($ssProd, $ssVendor, $ssD, $ssU) {\n    $this->ssProductName = $ssProd;\n    $this->ssVendorName = $ssVendor;\n    $this->ssDescr = $ssD;\n    $this->ssURI = $ssU;\n}\n\nfunction DisplayOneEntry() {\n    echo '<table border="1">\n                <tr>\n                <td>'.$this->ssProductName.'</td>\n                <td>'.$this->ssVendorName.'</td>\n                <td>'.$this->ssDescr.'</td>\n                <td>'.$this->ssURI.'</td>\n                </tr>\n                </table>';\n}