$ftp_server = "ftp.example.com";\n$ftp_port = 21;\n$ftp_timeout = 10;\n\n// Connect to the FTP server\n$conn = ftp_connect($ftp_server, $ftp_port, $ftp_timeout);\n\nif ($conn !== false) {\n    // Login to the FTP server (anonymous login)\n    $login = ftp_login($conn, "anonymous", "");\n\n    if ($login !== false) {\n        // Change directory to the specified directory\n        $dir = "/path/to/directory";\n        $chdir = ftp_chdir($conn, $dir);\n\n        if ($chdir !== false) {\n            // Get the last error message\n            $error = ftp_last_error($conn);\n\n            if ($error === "") {\n                // Everything is fine, print a success message\n                print "FTP server is up and running.\n";\n            } else {\n                // Print the error message\n                print "FTP error: $error\n";\n            }\n        } else {\n            print "Failed to change directory.\n";\n        }\n    } else {\n        print "Failed to login to the FTP server.\n";\n    }\n\n    // Close the FTP connection\n    ftp_close($conn);\n} else {\n    print "Failed to connect to the FTP server.\n";\n}