$sql = "SELECT * FROM table LIMIT 0,1";\n$result = mysqli_query($yourConnection, $sql);\n\nif ($result) {\n    $data = mysqli_fetch_assoc($result);\n    $variables = [];\n\n    foreach ($data as $key => $value) {\n        // Sanitize or validate $value if necessary\n        $variables[$key] = $value;\n    }\n\n    // Now you can access your variables like $variables['column_name']\n    print_r($variables);\n} else {\n    echo "Error: " . mysqli_error($yourConnection);\n}\n\nmysqli_close($yourConnection);