function postTweet($tweet) {\n    $retryCount = 0;\n    $maxRetries = 3;\n    $backoffTime = 10; // initial backoff time in seconds\n\n    while ($retryCount < $maxRetries) {\n        try {\n            $tweet->update('$link');\n            break;\n        } catch (TwitterException $e) {\n            if ($e->getCode() == 403) { // rate limit exceeded\n                sleep($backoffTime);\n                $backoffTime *= 2; // exponential backoff\n                $retryCount++;\n            } else {\n                throw $e;\n            }\n        }\n    }\n}