require_once __DIR__ . '/vendor/autoload.php';\n\n$client = new Google_Client();\n$client->setApplicationName("Client_Google_Maps");\n$client->setDeveloperKey("YOUR_API_KEY");\n\n$service = new Google_Service_Maps($client);\n\n$markers = array(\n    array('lat' => 37.7749, 'lng' => -122.4194, 'title' => 'Marker 1'),\n    array('lat' => 37.7859, 'lng' => -122.4364, 'title' => 'Marker 2'),\n);\n\n$mapOptions = array(\n    'center' => array('lat' => 37.7749, 'lng' => -122.4194),\n    'zoom' => 12,\n    'markers' => $markers,\n);\n\n$mapUrl = "https://maps.googleapis.com/maps/api/staticmap";\n$mapUrl .= "?key=" . $client->getDeveloperKey();\n$mapUrl .= "&center=" . $mapOptions['center']['lat'] . "," . $mapOptions['center']['lng'];\n$mapUrl .= "&zoom=" . $mapOptions['zoom'];\n$mapUrl .= "&size=600x300";\n$mapUrl .= "&maptype=roadmap";\n$mapUrl .= "&markers=" . implode("|", array_map(function($marker) {\n    return $marker['lat'] . "," . $marker['lng'];\n}, $markers));\n\necho "<img src='$mapUrl'>";