function quicksort($arr) {\n    if (count($arr) <= 1) {\n        return $arr;\n    }\n    $pivot = $arr[0];\n    $left = array_filter($arr, function ($x) use ($pivot) {\n        return $x < $pivot;\n    });\n    $middle = array_filter($arr, function ($x) use ($pivot) {\n        return $x == $pivot;\n    });\n    $right = array_filter($arr, function ($x) use ($pivot) {\n        return $x > $pivot;\n    });\n    return array_merge(quicksort($left), $middle, quicksort($right));\n}