class ClassA {\n  private $privateValue = 'private';\n  public $publicValue = 'public';\n\n  function __get($item) {\n    if ($item == 'privateValue') {\n      return $this->privateValue;\n    }\n  }\n}\n\n$a = new ClassA();\necho $a->publicValue; // outputs: public\necho $a->privateValue; // outputs: private\necho $a->nonExistentValue; // outputs: Notice: Undefined property: ClassA::$nonExistentValue