// StranglerController.php\nclass StranglerController extends Zend_Controller_Action\n{\n    public function indexAction()\n    {\n        // Check if the request is for a new feature\n        if ($request->getPathInfo() == '/new-feature/*') {\n            // Route to the new code\n            $this->_forward('index', 'index', 'new-feature');\n        } else {\n            // Route to the "old" application\n            // ...\n        }\n    }\n}