// Create the TSV file\n$tsv_data = "Order ID\tProduct Name\tQuantity\tCustomer Name\tAddress\n";\n$tsv_data .= "12345\tProduct A\t2\tJohn Doe\t123 Main St\n";\n$tsv_data .= "67890\tProduct B\t3\tJane Doe\t456 Elm St\n";\n\n$tsv_file = 'orders.tsv';\nfile_put_contents($tsv_file, $tsv_data);\n\n// FTP upload\n$ftp_server = 'ftp.example.com';\n$ftp_username = 'username';\n$ftp_password = 'password';\n\n$conn_id = ftp_connect($ftp_server);\n$login_result = ftp_login($conn_id, $ftp_username, $ftp_password);\n\nif ($login_result) {\n    ftp_put($conn_id, 'orders.tsv', $tsv_file, FTP_ASCII);\n    ftp_close($conn_id);\n} else {\n    echo "FTP login failed";\n}