<?php\n$raw_email = file_get_contents('php://stdin'); // Read raw email from stdin\n\n// Parse the email\n$mailparse_msg = mailparse_msg_create();\nmailparse_msg_parse($mailparse_msg, $raw_email);\n\n// Extract the email parts\n$structure = mailparse_msg_get_structure($mailparse_msg);\n$parts = mailparse_msg_get_part($mailparse_msg, $structure);\n\n// Loop through the email parts\nforeach ($parts as $part_id) {\n    $part = mailparse_msg_get_part($mailparse_msg, $part_id);\n    $data = mailparse_msg_get_part_data($part);\n\n    // Do something with the part data (e.g., extract headers, body, etc.)\n    print_r($data);\n}\n\n// Clean up\nmailparse_msg_free($mailparse_msg);