$sock = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);\nsocket_bind($sock, $local, $port) or die('Could not bind to address');\n\n// Set the socket to non-blocking mode\nsocket_set_nonblock($sock);\n\nwhile (1) {\n    $data = socket_read($sock, 1024, PHP_NORMAL_READ);\n    if ($data !== false) {\n        echo $data;\n    } else {\n        // No data available, sleep for a short time before trying again\n        usleep(10000); // 10ms\n    }\n}\n\nsocket_close($sock);