// license.php\n$serial_number = 'GENERATED_SERIAL_NUMBER';\n$license_server = 'https://your-license-server.com/verify';\n\n$ch = curl_init($license_server);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, array('serial_number' => $serial_number));\n$response = curl_exec($ch);\ncurl_close($ch);\n\nif ($response !== 'VALID') {\n    exit('Invalid license');\n}