function getLeafNodes($array) {\n    $stack = array($array);\n    $leafNodes = array();\n\n    while (!empty($stack)) {\n        $current = array_pop($stack);\n        foreach ($current as $value) {\n            if (is_array($value)) {\n                array_push($stack, $value);\n            } else {\n                $leafNodes[] = $value;\n            }\n        }\n    }\n\n    return $leafNodes;\n}