class ReturnValue {\n    private $value;\n\n    public function __construct($value) {\n        $this->value = $value;\n    }\n\n    public function __toString() {\n        return (string) $this->value;\n    }\n}\n\nfunction fn() {\n    $result = 'Hello, World!';\n    return new ReturnValue($result);\n}\n\necho fn(); // outputs "Hello, World!"\n$var = fn();\nvar_dump($var); // outputs object(ReturnValue)#1 (1) { ["value":private]=> string(13) "Hello, World!" }