function getLeafNodes($array) {\n    $stack = array($array);\n    $leafNodes = array();\n\n    while (!empty($stack)) {\n        $current = array_pop($stack);\n        foreach ($current as $value) {\n            if (is_array($value)) {\n                array_push($stack, $value);\n            } elseif (is_object($value)) {\n                // Assuming Seller object has a method to return its properties as an array\n                // Replace 'getProperties' with actual method name if available\n                $objectProperties = get_object_vars($value);\n                foreach ($objectProperties as $propertyValue) {\n                    $leafNodes[] = $propertyValue;\n                }\n            } else {\n                $leafNodes[] = $value;\n            }\n        }\n    }\n\n    return $leafNodes;\n}