<?php\nrequire_once 'oauth.php';\n\n$consumerKey = 'YOUR_CONSUMER_KEY';\n$consumerSecret = 'YOUR_CONSUMER_SECRET';\n$callbackUrl = 'YOUR_CALLBACK_URL';\n\n$oauth = new OAuth($consumerKey, $consumerSecret, OAUTH_SIG_METHOD_HMACSHA1, OAUTH_AUTH_TYPE_URI);\n$oauth->setTokenURL('https://api.login.yahoo.com/oauth/v2/get_token');\n$oauth->setAuthURL('https://api.login.yahoo.com/oauth/v2/request_auth');\n\n// Get request token\n$requestToken = $oauth->getRequestToken($callbackUrl);\n\n// Authorize\n$authorizeUrl = $oauth->getAuthorizeURL($requestToken);\nheader('Location: ' . $authorizeUrl);\n\n// Get access token\n$accessToken = $oauth->getAccessToken($requestToken);\n\n// Send message using Yahoo API\n$message = 'Hello, World!';\n$yahooApiUrl = 'https://api.messenger.yahoo.com/v1/message/send';\n$headers = array(\n    'Authorization: Bearer ' . $accessToken,\n    'Content-Type: application/json'\n);\n$data = array(\n    'message' => $message,\n    'to' => 'recipient_yahoo_id'\n);\n$jsonData = json_encode($data);\n\n$ch = curl_init($yahooApiUrl);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $jsonData);\ncurl_setopt($ch, CURLOPT_HTTPHEADER, $headers);\n$response = curl_exec($ch);\ncurl_close($ch);\n\necho $response;