<?php\n  if (isset($_POST['submit'])) {\n    $to = 'your_email@example.com'; // recipient email\n    $subject = 'Resume Submission';\n    $name = $_POST['name'];\n    $email = $_POST['email'];\n    $resume = $_POST['resume'];\n\n    $headers = 'From: ' . $email . "\r\n";\n    $headers .= 'MIME-Version: 1.0' . "\r\n";\n    $headers .= 'Content-Type: text/html; charset=UTF-8' . "\r\n";\n\n    $message = '<html><body>';\n    $message .= '<h1>Resume Submission</h1>';\n    $message .= '<p>Name: ' . $name . '</p>';\n    $message .= '<p>Email: ' . $email . '</p>';\n    $message .= '<p>Resume: ' . $resume . '</p>';\n    $message .= '</body></html>';\n\n    if (mail($to, $subject, $message, $headers)) {\n      echo 'Thank you for submitting your resume!';\n    } else {\n      echo 'Error submitting resume. Please try again.';\n    }\n  }\n?>\n\n<form method="post">\n  <input type="text" name="name" placeholder="Name">\n  <input type="email" name="email" placeholder="Email">\n  <textarea name="resume" placeholder="Paste your resume here"></textarea>\n  <button type="submit" name="submit">Submit Resume</button>\n</form>