// Example of Microsoft Graph API call in PHP to get contacts\n$clientId = "your_client_id";\n$clientSecret = "your_client_secret";\n$tenantId = "your_tenant_id";\n$access_token = getAccessToken($clientId, $clientSecret, $tenantId);\n\n$curl = curl_init();\ncurl_setopt($curl, CURLOPT_URL, "https://graph.microsoft.com/v1.0/me/contacts");\ncurl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($curl, CURLOPT_HTTPHEADER, array(\n    "Authorization: Bearer $access_token"\n));\n$response = curl_exec($curl);\ncurl_close($curl);