$today = date('Y-m-d');\n\n$stmt = $conn->prepare("SELECT * \n                        FROM your_table \n                        WHERE ? BETWEEN date_from AND date_to");\n\n$stmt->bind_param("s", $today);\n$stmt->execute();\n\n$result = $stmt->get_result();\n\nif ($result->num_rows > 0) {\n    echo "There are requests for today's date.";\n} else {\n    echo "No requests for today's date.";\n}