// Example of Google People API call in PHP to get contacts\n$clientId = "your_client_id";\n$clientSecret = "your_client_secret";\n$redirectUri = "your_redirect_uri";\n$access_token = getAccessTokenGoogle($clientId, $clientSecret, $redirectUri);\n\n$curl = curl_init();\ncurl_setopt($curl, CURLOPT_URL, "https://people.googleapis.com/v1/people/me/connections");\ncurl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($curl, CURLOPT_HTTPHEADER, array(\n    "Authorization: Bearer $access_token"\n));\n$response = curl_exec($curl);\ncurl_close($curl);