class DB {\n    // ...\n\n    public static function executeQuery($query, $params = array()) {\n        try {\n            $db = self::getInstance();\n            $stmt = $db->prepare($query);\n            $stmt->execute($params);\n            return $stmt->fetchAll();\n        } catch (PDOException $e) {\n            // Log the error or return a custom error message\n            return array('error' => 'Database error');\n        }\n    }\n}