<?php\nsession_start();\n\n$fileName = $_POST['textFile'];\n$cacheKey = 'file_reader_result_'.$fileName;\n\n// Check if result is cached\n$redis = new Redis();\n$redis->connect('localhost', 6379);\nif ($redis->exists($cacheKey)) {\n    $result = $redis->get($cacheKey);\n} else {\n    // Make request to external script using curl\n    $ch = curl_init($_SESSION['serverURL']."fileReader.php?textFile=$fileName");\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    $result = curl_exec($ch);\n    curl_close($ch);\n\n    // Cache the result\n    $redis->set($cacheKey, $result);\n    $redis->expire($cacheKey, 3600); // Cache for 1 hour\n}\n\necho $result;\n?>