// Set the number of images to preload\n$preload_count = 10;\n\n// Set the JSON data source\n$json_data = 'image_data.json';\n\n// Load the initial set of images\n$images = json_decode(file_get_contents($json_data), true);\n$initial_images = array_slice($images, 0, $preload_count);\n\n// Preload the initial set of images\nforeach ($initial_images as $image) {\n    echo '<img src="' . $image['url'] . '" alt="' . $image['caption'] . '" style="display:none;">';\n}\n\n// Set up the navigation links\necho '<a href="#" class="prev">Prev</a>';\necho '<a href="#" class="next">Next</a>';\n\n// Set up the AJAX handler\necho '<script>';\necho '  var current_index = 0;';\necho '  var image_count = ' . count($images) . ';';\necho '  var preload_count = ' . $preload_count . ';';\necho '  var json_data = \'' . $json_data . '\';';\necho '  ';\necho '  $(".prev").click(function() {';\necho '    if (current_index > 0) {';\necho '      current_index -= 1;';\necho '      loadImages();';\necho '    }';\necho '  });';\necho '  ';\necho '  $(".next").click(function() {';\necho '    if (current_index < image_count - 1) {';\necho '      current_index += 1;';\necho '      loadImages();';\necho '    }';\necho '  });';\necho '  ';\necho '  function loadImages() {';\necho '    $.ajax({';\necho '      type: "GET",';\necho '      url: json_data,';\necho '      data: {';\necho '        start: current_index,';\necho '        count: preload_count';\necho '      },';\necho '      success: function(data) {';\necho '        var images = JSON.parse(data);';\necho '        $("#image-container").html("");';\necho '        $.each(images, function(index, image) {';\necho '          $("#image-container").append("<img src=\"" + image.url + "\" alt=\"" + image.caption + "\">");';\necho '        });';\necho '      }';\necho '    });';\necho '  }';\necho '</script>';