// Create a new plugin file (e.g., custom-post-fields.php)\n// and add the following code\n\nfunction add_custom_fields_to_post_edit_page() {\n    // Add a new metabox to the post editing page\n    add_meta_box(\n        'custom_fields_metabox',\n        'Custom Fields',\n        'custom_fields_metabox_callback',\n        'post',\n        'advanced',\n        'high'\n    );\n}\n\nfunction custom_fields_metabox_callback($post) {\n    // Add your custom fields here\n    echo '<label for="custom_field_1">Custom Field 1:</label>';\n    echo '<input type="text" id="custom_field_1" name="custom_field_1" value="' . get_post_meta($post->ID, 'custom_field_1', true) . '">';\n}\n\nadd_action('add_meta_boxes', 'add_custom_fields_to_post_edit_page');