class MyClass {\n    private $dispatch = array(\n        1 => 'someFunction',\n        2 => 'anotherFunction'\n    );\n\n    private function someFunction() {\n        echo "someFunction called\n";\n    }\n\n    private function anotherFunction() {\n        echo "anotherFunction called\n";\n    }\n\n    public function dispatch($id) {\n        if (isset($this->dispatch[$id])) {\n            call_user_func(array($this, $this->dispatch[$id]));\n        } else {\n            echo "Invalid dispatch ID\n";\n        }\n    }\n}\n\n$obj = new MyClass();\n$obj->dispatch(1); // outputs "someFunction called"\n$obj->dispatch(2); // outputs "anotherFunction called"