require_once 'vendor/autoload.php';\n\nuse PHPAuth\Auth;\nuse PHPAuth\DataSource;\n\n$dbh = new PDO('mysql:host=localhost;dbname=your_database', 'your_username', 'your_password');\n\n$auth = new Auth($dbh, new DataSource());\n\n// Register a new user\n$auth->register('username', 'password', 'email@example.com');\n\n// Login a user\n$auth->login('username', 'password');\n\n// Protect a page\nif (!$auth->isLoggedin()) {\n    header('Location: login.php');\n    exit;\n}