// Controller\nclass MessageController {\n    public function index() {\n        $messages = Message::all(); // Retrieve messages from the Model\n        return view('messages.index', ['messages' => $messages]);\n    }\n}\n\n// View (using a template engine like Blade)\n// messages/index.blade.php\n@foreach ($messages as $message)\n    <tr>\n        <td>{{ $message->text }}</td>\n    </tr>\n@endforeach\n\n// Model\nclass Message {\n    public static function all() {\n        // Retrieve messages from the database using prepared statements\n        $stmt = $db->prepare('SELECT * FROM messages');\n        $stmt->execute();\n        return $stmt->fetchAll();\n    }\n}