$args = array(\n    'cat' => 1, // category ID\n    'posts_per_page' => -1, // get all posts\n    'orderby' => 'title',\n    'order' => 'ASC'\n);\n\n$posts = get_posts($args);\n$posts_per_row = 3;\n\nif ($posts) {\n    $i = 0;\n    foreach ($posts as $post) {\n        if ($i % $posts_per_row == 0) {\n            echo '<li>';\n        }\n        echo '<a href="' . get_permalink($post->ID) . '">' . get_the_title($post->ID) . '</a> | ';\n        $i++;\n        if ($i % $posts_per_row == 0 || $i == count($posts)) {\n            echo '</li>';\n        }\n    }\n}