// Set cache file path and expiration time (1 hour)\n$cacheFile = 'weather_cache.xml';\n$cacheExpiration = 3600; // 1 hour\n\n// Check if cache file exists and is not expired\nif (file_exists($cacheFile) && filemtime($cacheFile) > time() - $cacheExpiration) {\n    // Load cached data\n    $xml = simplexml_load_file($cacheFile);\n} else {\n    // Fetch new data and cache it\n    $xml = simplexml_load_file('http://www.weather.gov/data/current_obs/KBED.xml');\n    file_put_contents($cacheFile, $xml->asXML());\n}\n\n// Extract and display weather data\n$temp = $xml->temp_f.' Degrees';\n$wind = $xml->wind_mph;\n$wind_dir = $xml->wind_dir;\n$gust = $xml->wind_gust_mph;\n$time = $xml->observation_time;\n$pres = $xml->pressure_in;\n$weath = $xml->weather;\n\n// Display weather data\necho "Temperature: $temp";\necho "Wind: $wind mph ($wind_dir)";\necho "Gust: $gust mph";\necho "Time: $time";\necho "Pressure: $pres in";\necho "Weather: $weath";