$servers = array('server1', 'server2', 'server3');\n$currentServerIndex = 0;\n\nfunction get_next_server() {\n    global $servers, $currentServerIndex;\n    $nextServer = $servers[$currentServerIndex];\n    $currentServerIndex = ($currentServerIndex + 1) % count($servers);\n    return $nextServer;\n}\n\n// Usage:\n$nextServer = get_next_server();\n// send the processing job to $nextServer