$url = 'http://source.com/file.mp3';\n\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_NOBODY, true); // Don't download the file, just get the headers\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_HEADER, true);\n\n$response = curl_exec($ch);\n$info = curl_getinfo($ch);\n\necho 'Last Modified: ' . $info['filetime'] . "\n";\necho 'Content Length: ' . $info['download_content_length'] . "\n";\n\ncurl_close($ch);