if (array_key_exists('update', $_POST)) {\n    $conn->begin_transaction();\n\n    try {\n        $sql1 = 'UPDATE photos SET photo_title = ? WHERE photo_id = ?';\n        $stmt1 = $conn->prepare($sql1);\n        $stmt1->bind_param('si', $_POST['photo_title'], $_POST['photo_id']);\n        $stmt1->execute();\n\n        $sql2 = 'UPDATE photographers SET photographer_name = ? WHERE photographer_id = (SELECT photographer_id FROM photos WHERE photo_id = ?)';\n        $stmt2 = $conn->prepare($sql2);\n        $stmt2->bind_param('si', $_POST['photographer_name'], $_POST['photo_id']);\n        $stmt2->execute();\n\n        $conn->commit();\n    } catch (Exception $e) {\n        $conn->rollback();\n        throw $e;\n    }\n}