function include_paths($paths) {\n    $include_paths = array();\n    foreach ($paths as $path) {\n        $files = glob($path . '/*.php', GLOB_NOSORT);\n        $subdirs = glob($path . '/*', GLOB_ONLYDIR | GLOB_NOSORT);\n        $include_paths = array_merge($include_paths, $files);\n        foreach ($subdirs as $subdir) {\n            $include_paths = array_merge($include_paths, include_paths(array($subdir)));\n        }\n    }\n    return $include_paths;\n}\n\n$paths = array('/path/to/dir1', '/path/to/dir2');\n$include_paths = include_paths($paths);\nset_include_path(implode(PATH_SEPARATOR, $include_paths));