function getForumData($query) {\n    $data = array();\n    while ($row = mysql_fetch_array($query)) {\n        $data[] = array(\n            'name' => $row['forum_name'],\n            'date' => $row['forum_date'],\n            'desc' => $row['forum_description'],\n            'lastpost' => $row['forum_lastpost']\n        );\n    }\n    return $data;\n}\n\n$forumData = getForumData($query);