// app/Model/MenuModel.php\nclass Menu extends AppModel {\n    public function getMenuItems() {\n        // Retrieve menu items from multiple tables using CakePHP's ORM\n        $menuItems = $this->find('all', array(\n            'conditions' => array('Menu.is_active' => 1),\n            'recursive' => 1,\n            'order' => array('Menu.order' => 'ASC')\n        ));\n        return $menuItems;\n    }\n}