function do_something($some_argument) {\n    $backtrace = debug_backtrace();\n    $variableName = getVariableName($backtrace);\n    echo $variableName; // Outputs: contact_name\n}\n\nfunction getVariableName($backtrace) {\n    $line = file($backtrace[0]['file']);\n    $line = $line[$backtrace[0]['line'] - 1];\n    $matches = array();\n    preg_match('/\((.*?)\)/', $line, $matches);\n    $matches = explode(',', $matches[1]);\n    return trim($matches[0]);\n}\n\n$contact_name = 'foo';\ndo_something($contact_name);