$stmt = $conn->prepare("INSERT INTO books (title, author, isbn, publisher, year, summary) VALUES (:title, :author, :isbn, :publisher, :year, :summary)");\n$stmt->bindParam(':title', $_POST['title']);\n$stmt->bindParam(':author', $_POST['author']);\n$stmt->bindParam(':isbn', $_POST['isbn']);\n$stmt->bindParam(':publisher', $_POST['publisher']);\n$stmt->bindParam(':year', $_POST['year']);\n$stmt->bindParam(':summary', $_POST['summary']);\n$stmt->execute();