$Router = $this->_front->getRouter();\n$currentRequest = $this->_front->getRequest();\n$currentCategory = $currentRequest->getParam('category');\n\n$CategoryRoute = new Zend_Controller_Router_Route('category/:category/:controller/:action/*',\n    array(\n        'controller' => 'index',\n        'action'     => 'index',\n        'category'   => $currentCategory // Dynamically set the default category\n    )\n);\n\n$Router->addRoute('category', $CategoryRoute);