class User extends CI_Controller {\n    public function __construct() {\n        parent::__construct();\n    }\n\n    public function index() {\n        // Load the profile box view and pass data to it\n        $data['username'] = 'John Doe';\n        $data['profile_info'] = 'This is a sample profile info';\n        $this->load->view('profile_box', $data);\n    }\n\n    public function get_profile_box() {\n        // Load the profile box view and return its output\n        $data['username'] = 'John Doe';\n        $data['profile_info'] = 'This is a sample profile info';\n        $output = $this->load->view('profile_box', $data, TRUE);\n        return $output;\n    }\n}