class Test\n{\n    public function test($methodName, ...$args) {\n        if (method_exists($this, $methodName)) {\n            call_user_func_array([$this, $methodName], $args);\n        } else {\n            throw new Exception("Method $methodName does not exist");\n        }\n    }\n\n    private function a($param1, $param2) {\n        echo("a with params: $param1, $param2");\n    }\n\n    private function b() {\n        echo("b");\n    }\n}\n\n$testObj = new Test();\n$testObj->test("a", "param1 value", "param2 value");\n$testObj->test("b");