<?php\n// Configuration\n$dbHost = 'localhost';\n$dbUser = 'your_username';\n$dbPass = 'your_password';\n$dbName = 'your_database_name';\n$tableName = 'your_table_name'; // Make sure this table exists in your database\n\n// Connect to the database\n$conn = mysqli_connect($dbHost, $dbUser, $dbPass, $dbName);\n\n// Check connection\nif (!$conn) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n// Check if the form has been submitted\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Check if options were selected\n    if (isset($_POST['options'])) {\n        $options = $_POST['options']; // This is an array of selected options\n\n        // Prepare the SQL query\n        $sql = "INSERT INTO $tableName (option_name) VALUES (?)";\n\n        // Prepare the statement\n        $stmt = mysqli_prepare($conn, $sql);\n\n        // Bind variables to the prepared statement as parameters\n        mysqli_stmt_bind_param($stmt, "s", $option);\n\n        // Loop through each selected option and insert into the database\n        foreach ($options as $option) {\n            // Execute the prepared statement\n            if (!mysqli_stmt_execute($stmt)) {\n                echo "Error inserting option: " . mysqli_stmt_error($stmt);\n            }\n        }\n\n        // Close the statement and database connection\n        mysqli_stmt_close($stmt);\n        mysqli_close($conn);\n\n        echo "Options inserted successfully.";\n    } else {\n        echo "No options were selected.";\n    }\n}\n?>