$bl_ftp_host = 'your_ftp_host';\n$bl_ftp_user = 'your_ftp_username';\n$bl_ftp_password = 'your_ftp_password';\n$ftp_path = "ftp://$bl_ftp_user:$bl_ftp_password@$bl_ftp_host/";\n\n$stream_options = array('ftp' => array('overwrite' => false));\n$context = stream_context_create($stream_options);\n\nif ($dh = opendir($ftp_path, $context)) {\n    while (false !== ($filename = readdir($dh))) {\n        if ($filename !== '.' && $filename !== '..') {\n            echo "File: $filename\n";\n\n            // Download the file\n            $remoteFile = $ftp_path . $filename;\n            $localFile = $filename; // You might want to specify a different path here\n            if (copy($remoteFile, $localFile)) {\n                echo "Successfully downloaded $filename\n";\n            } else {\n                echo "Failed to download $filename\n";\n            }\n        }\n    }\n    closedir($dh);\n} else {\n    echo "Could not open the FTP directory.\n";\n}