$frontController = Zend_Controller_Front::getInstance();\n$modules = $frontController->getControllerDirectory();\n\n$aclData = array();\n\nforeach ($modules as $moduleName => $modulePath) {\n    $moduleDir = new DirectoryIterator($modulePath);\n    foreach ($moduleDir as $fileInfo) {\n        if ($fileInfo->isFile() && $fileInfo->getExtension() == 'php') {\n            $controllerName = substr($fileInfo->getFilename(), 0, -4);\n            $controllerClass = ucfirst($moduleName) . '_' . ucfirst($controllerName) . 'Controller';\n            $reflectionClass = new ReflectionClass($controllerClass);\n            $methods = $reflectionClass->getMethods(ReflectionMethod::IS_PUBLIC);\n            foreach ($methods as $method) {\n                if ($method->isAction()) {\n                    $actionName = substr($method->getName(), 0, -6);\n                    $aclData[$moduleName][$controllerName][$actionName] = array(\n                        'module' => $moduleName,\n                        'controller' => $controllerName,\n                        'action' => $actionName\n                    );\n                }\n            }\n        }\n    }\n}