$xml = simplexml_load_string(file_get_contents('your_xml_file.xml'));\n$namespaces = $xml->getNameSpaces(true);\n\nfunction removeNamespace(SimpleXMLElement $xml, $namespace) {\n    foreach ($xml->children() as $child) {\n        if (strpos($child->getName(), $namespace) === 0) {\n            $child->setName(str_replace($namespace, '', $child->getName()));\n        }\n        if ($child->count()) {\n            removeNamespace($child, $namespace);\n        }\n    }\n}\n\nforeach ($namespaces as $namespace => $value) {\n    removeNamespace($xml, $namespace);\n}\n\n$xmlString = $xml->asXML();